import React from 'react';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import { Button} from '@material-ui/core';
import {
    ThemeProvider,
    createMuiTheme,
    fade
  } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import InputBase from '@material-ui/core/InputBase';
import HotTopics from '../../../components/profile/Hot-topics';
import SideNav from '../../../containers/nav'


const BootstrapInput = withStyles(theme => ({
    root: {
      'label + &': {
        marginTop: theme.spacing(3),
      },
    },
    input: {
      borderRadius: 20,
      position: 'relative',
      backgroundColor: '#e4f2f0',
      border: '1px solid #ced4da',
      fontSize: 16,
      width: '650px',
      padding: '10px 12px',
      transition: theme.transitions.create(['border-color', 'box-shadow']),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(','),
      '&:focus': {
        boxShadow: `${fade(theme.palette.primary.main, 0.25)} 0 0 0 0.2rem`,
        borderColor: theme.palette.primary.main,
      },
    },

  }))(InputBase);



const BootstrapExceptional = withStyles(theme => ({
    root: {
      'label + &': {
        marginTop: theme.spacing(3),
      },
    },
    input: {
      borderRadius: 20,
      position: 'relative',
      backgroundColor: '#e4f2f0',
      border: '1px solid #ced4da',
      fontSize: 16,
      width: '150px',
      padding: '10px 12px',
      transition: theme.transitions.create(['border-color', 'box-shadow']),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        '-apple-system',
        'BlinkMacSystemFont',
        '"Segoe UI"',
        'Roboto',
        '"Helvetica Neue"',
        'Arial',
        'sans-serif',
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(','),
      '&:focus': {
        boxShadow: `${fade(theme.palette.primary.main, 0.25)} 0 0 0 0.2rem`,
        borderColor: theme.palette.primary.main,
      },
    },

  }))(InputBase);


  const theme = createMuiTheme({
    palette: {
      primary: green,
    },
  });


const StyledTabs = withStyles({
    indicator: {
      display: 'flex',
      justifyContent: 'center',
      backgroundColor: 'transparent',
      '& > div': {
        maxWidth: 40,
        width: '100%',
        backgroundColor: '#6fc1a5',
      },
    },
  })(props => <Tabs {...props} TabIndicatorProps={{ children: <div /> }} />);

  const StyledTab = withStyles(theme => ({
    root: {
      textTransform: 'none',
      color: '#6fc1a5',
      fontWeight: theme.typography.fontWeightRegular,
      fontSize: theme.typography.pxToRem(15),
      marginRight: theme.spacing(1),
      '&:focus': {
        opacity: 1,
      },
    },
  }))(props => <Tab disableRipple {...props} />);


class WalletInfo extends React.Component{

    render() {


        return (
            <Grid container direction="row" justify="space-between" alignItems="flex-start" spacing={16}>
                <Grid item xs={8}>
            <Grid container spacing={2}>
              <Grid item xs={11}>
              <Paper> 
            <Container>

            <StyledTabs 
            // value={value} onChange={handleChange}
            aria-label="styled tabs example">
            <StyledTab  label="Buy more coins" />

            </StyledTabs>

            <Typography/>
            <Grid container>
            <Grid item xs={9}>
            Your Balance
            </Grid>
            <Grid item xs={3} style={{float: 'right'}}>
            1 GBP
            10 coins
            </Grid>
            </Grid>

            <Grid>
            10594 coins

            </Grid>

            <Grid>


      </Grid>
      <Grid>
      <ThemeProvider theme={theme}>
          <Grid container  wrap="nowrap" spacing={2}>
          <Grid item>       
            <BootstrapInput defaultValue="12796" id="bootstrap-input" />

                </Grid>
                </Grid>

                <Grid container  wrap="nowrap" spacing={2}>

        <Grid item>
<BootstrapInput defaultValue="1279/6GBP" id="bootstrap-input" />

                </Grid>
                </Grid>
                <Grid container  wrap="nowrap" spacing={2}>

                <Grid item>

                <BootstrapInput defaultValue="Card holder" id="bootstrap-input" />

                </Grid>
                </Grid>
                <Grid container  wrap="nowrap" spacing={2}>
                <Grid item>

                <BootstrapInput defaultValue="Card number" id="bootstrap-input" />

            </Grid> 
            </Grid>
                <Grid container  wrap="nowrap" spacing={2}>
                <Grid item>

                     <Grid xs={6}>
                        <BootstrapExceptional defaultValue="expiry date" id="bootstrap-exceptional" />
                    </Grid>
                    </Grid>
                    <Grid item>


                    <Grid xs={6}>
                        <BootstrapExceptional defaultValue="cvc/cvv" id="bootstrap-exceptional" />
                    </Grid>
                    </Grid>  
                </Grid>               
                </ThemeProvider>
                <Grid>


        </Grid>
          </Grid>
          <Grid>
          <br></br>
          Remember this action is irrerversible
          </Grid>
          <Button style={{borderRadius: '25px', width:'130px' , backgroundColor:'#6fc1a5'}} variant="contained"  color='primary'>
            Send
           </Button>

          </Container>
          </Paper>
          </Grid>
                          </Grid>

                          </Grid>
                <Grid item xs={4}>
                <HotTopics />
                </Grid>
            </Grid>







        );
    }
}

export default WalletInfo;
